import React from 'react';
import './App.css';
import TopBar from './TopBar';
import BlogPost from './BlogPost';
import summerImage from './images/summer.webp';

const blogPosts = [
  {
    id: 1,
    title: "Summer Fashion Trends 2024",
    date: "June 1, 2024",
    author: "Taranjot Kaur",
    image: summerImage,
    content: "Discover the latest summer fashion trends for 2024. This season is all about vibrant colors, light fabrics, and bold patterns. Here are the top trends to keep you stylish this summer:",
    trends: [
      "Bright Neon Colors",
      "Light Linen Fabrics",
      "Bold Floral Prints",
      "Casual Chic Styles",
      "Accessorizing with Straw Hats and Sunglasses"
    ]
  }
];

function App() {
  return (
    <div className="App">
      <TopBar />
      <div className="content">
        {blogPosts.map(post => (
          <BlogPost
            key={post.id}
            title={post.title}
            date={post.date}
            author={post.author}
            image={post.image}
            content={post.content}
            trends={post.trends}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
