import React from 'react';
import './App.css';
import Logo from './Logo'; // Import Logo component
import NavigationBar from './NavigationBar'; // Import NavigationBar component
import TopBar from './TopBar';
import BlogPost from './BlogPost';
import MyComponent from './MyComponent'; // Import MyComponent

const blogPosts = [
  {
    id: 1,
    title: "Summer Fashion Trends 2024",
    date: "June 1, 2024",
    author: "Taranjot Kaur",
    image: 'summer.webp',
    content: "Discover the latest summer fashion trends for 2024. This season is all about vibrant colors, light fabrics, and bold patterns. Here are the top trends to keep you stylish this summer:",
    trends: [
      "Bright Neon Colors",
      "Light Linen Fabrics",
      "Bold Floral Prints",
      "Casual Chic Styles",
      "Accessorizing with Straw Hats and Sunglasses"
    ]
  },
  {
    id: 2,
    title: "Winter Fashion Trends 2024",
    date: "December 1, 2024",
    author: "John Doe",
    image: 'winter.jpg',
    content: "Stay cozy and stylish with the latest winter fashion trends for 2024. From warm layers to statement accessories, here's what you need to know to stay on-trend this winter:",
    trends: [
      "Chunky Knit Sweaters",
      "Faux Fur Coats",
      "Plaid Patterns",
      "Statement Boots",
      "Layering with Scarves and Hats"
    ]
  }
];

function App() {
  // Assume the user's name is passed as a prop
  const userName = "John Doe";

  return (
    <div className="App">
      {/* Render Logo on the left-hand side */}
      <Logo />

      {/* Render NavigationBar with user's name on the right-hand side */}
      <NavigationBar userName={userName} />

      {/* Rest of the content */}
      <div className="content">
        <TopBar />
        <MyComponent /> {/* MyComponent is used here */}
        {blogPosts.map(post => (
          <React.Fragment key={post.id}>
            <BlogPost
              title={post.title}
              date={post.date}
              author={post.author}
              image={post.image}
              content={post.content}
              trends={post.trends}
            />
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

export default App;
