import React from 'react';
import BlogPost from './BlogPost';
import './Blog.css';

const Blog = () => {
  return (
    <div className="blog">
      <BlogPost
        title="Summer Fashion Trends"
        date="May 31, 2024"
        author="Jane Doe"
        image="path/to/image1.jpg"
      >
        <p>Discover the latest trends in summer fashion, from vibrant colors to airy fabrics...</p>
      </BlogPost>
      <BlogPost
        title="Winter Wardrobe Essentials"
        date="May 15, 2024"
        author="John Smith"
        image="path/to/image2.jpg"
      >
        <p>Stay warm and stylish with these winter wardrobe essentials...</p>
      </BlogPost>
      {/* Add more BlogPost components as needed */}
    </div>
  );
};

export default Blog;
