import React from 'react';
import './App.css';
import TopBar from './TopBar';
import BlogPost from './BlogPost';
import MyComponent from './MyComponent'; // Import MyComponent

const blogPosts = [
  {
    id: 1,
    title: "Summer Fashion Trends 2024",
    date: "June 1, 2024",
    author: "Taranjot Kaur",
    image: 'summer.webp',
    content: "Discover the latest summer fashion trends for 2024. This season is all about vibrant colors, light fabrics, and bold patterns. Here are the top trends to keep you stylish this summer:",
    trends: [
      "Bright Neon Colors",
      "Light Linen Fabrics",
      "Bold Floral Prints",
      "Casual Chic Styles",
      "Accessorizing with Straw Hats and Sunglasses"
    ]
  },
  {
    id: 2,
    title: "Summer Fashion Trends 2024",
    date: "June 1, 2024",
    author: "Taranjot Kaur",
    image: 'winter.jpg',
    content: "Discover the latest summer fashion trends for 2024. This season is all about vibrant colors, light fabrics, and bold patterns. Here are the top trends to keep you stylish this summer:",
    trends: [
      "Bright Neon Colors",
      "Light Linen Fabrics",
      "Bold Floral Prints",
      "Casual Chic Styles",
      "Accessorizing with Straw Hats and Sunglasses"
    ]
  }
];

function App() {
  return (
    <div className="App">
      <TopBar />
      <MyComponent /> {/* MyComponent is used here */}
      <div className="content">
        {blogPosts.map(post => (
          <React.Fragment key={post.id}>
            <BlogPost
              title={post.title}
              date={post.date}
              author={post.author}
              image={post.image}
              content={post.content}
              trends={post.trends}
            />
            <MyComponent /> {/* MyComponent is used inside map function */}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

export default App;
